def graph_genav_genbest_best_vs_generations(best, gen_best, gen_av, title='PODI_Algorithm_Progress', path='results/', save=True):
    """
    Graph showing an algorithm's best, gen_best, gen_av over n generations
    """
    x = np.arange(0, len(best), 1)
    fig = plt.figure()
    plt.plot(x, best, color="blue", linewidth=2, linestyle="-", label='Overall Best')
    if gen_best is not None:
        plt.plot(x, gen_best, color="red", linewidth=1, linestyle="-", label='Generational Best')
    if gen_av is not None:
        plt.plot(x, gen_av, color="orange", linewidth=2, linestyle="-", label='Generational Average')

    plt.xlim(0, len(best)-1)
    if gen_av is not None:
        plt.ylim(0, max(max(gen_av), max(best)) * 1.1)
    else:
        plt.ylim(0, max(best) * 1.1)
    plt.title(title)
    plt.legend(loc='upper right')
    plt.xlabel('Generations', fontsize=11)
    plt.ylabel('Fitness', fontsize=11)

    if save:
        fig.savefig('../' + path + title + '.png')
    else:
        plt.show()


