def graph_genbest_vs_generations_many_algs(bests_dict, log=False, title='Comparison of Algorithm Progress',
                                           path='results/alg_comparison/', save=True, legend_loc='upper left'):
    """
    Graph showing an algorithm's gen_best over n generations
    """
    bests_dict = OrderedDict(sorted(bests_dict.items()))
    arb_element = list(bests_dict.keys())[0]
    # print(arb_element)

    x = np.arange(0, len(bests_dict[arb_element]), 1)
    # print('x shape:', np.shape(x))
    random.seed(101)
    # sns.color_palette("pastel")
    current_palette = sns.color_palette("husl", len(bests_dict))
    sns.set_palette(current_palette)
    # plt.style.use('seaborn')
    fig = plt.figure()

    all_b = []
    for alg_name, bests in bests_dict.items():
        if log:
            plt.semilogy(x, bests, linewidth=2, linestyle="-", label=alg_name)
        else:
            plt.plot(x, bests, linewidth=2, linestyle="-", label=alg_name)
        all_b += bests

    plt.xlim(0, len(bests_dict[arb_element]) - 1)
    # plt.ylim(0, max(all_b) * 1.1)
    plt.title(title)
    plt.legend(loc=legend_loc)
    plt.xlabel('Generations', fontsize=11)
    plt.ylabel('Fitness', fontsize=11)

    if save:
        fig.savefig('../' + path + title + '.png')
    else:
        plt.show()


