def graph_parameter_tuning_matrix(xvar, yvar, sols, path='param_results/', save=True, m_ind=None,
                                  title='Parameter Tuning Matrix', xy_labels=None, f_name='ParamTuning', logo=False):
    """
    Graph parameter tuning heatmap for 2 parameters
    """
    fig = plt.figure()
    if logo is True:
        plt.pcolor(xvar, yvar, sols, cmap=plt.cm.hot, norm=colors.LogNorm())
    else:
        plt.pcolor(xvar, yvar, sols, cmap=plt.cm.hot)
    plt.title(title)
    plt.xlim(0, 1)
    plt.ylim(0, 1)
    if xy_labels is not None:
        plt.xlabel(xy_labels[0])
        plt.ylabel(xy_labels[1])
    plt.colorbar()
    if m_ind is not None:
        plt.scatter(m_ind[0], m_ind[1], marker='o', c='b')
    if save:
        fig.savefig('../' + path + f_name + '.png')
    else:
        plt.show()


def graph_fitness_vs_generations(fitnesses):
    """
    Graph fitnesses vs. the number of generations used by an algorithm
    """
    x = np.arange(0, len(fitnesses), 1)
    plt.plot(x, fitnesses, color="blue", linewidth=2.5, linestyle="-")
    plt.show()

